import { useRouter } from "next/router";
import ErrorPage from "next/error";
import Container from "../../components/container";
import PostBody from "../../components/post-body";
import MoreStories from "../../components/more-stories";
import Header from "../../components/header";
import PostHeader from "../../components/post-header";
import SectionSeparator from "../../components/section-separator";
import Layout from "../../components/layout";
import { getPostDataById, getAllPostsWithId } from "../../lib/api";
import PostTitle from "../../components/post-title";
import Head from "next/head";
import { CMS_NAME } from "../../lib/constants";
import markdownToHtml from "../../lib/markdownToHtml";

export default function Post({ post, morePosts, preview }) {
  const router = useRouter();
  console.log("POST", post);
  if (!router.isFallback && !post[0]?.id) {
    return <ErrorPage statusCode={404} />;
  }
  return (
    <Layout preview={preview}>
      <Container>
        <Header />
        {/* {router.isFallback ? (
          <PostTitle>Loadingâ€¦</PostTitle>
        ) : (
          <>
            <article>
              <Head>
                <title>
                  {post.title} | Next.js Blog Example with {CMS_NAME}
                </title>
                <meta property="og:image" content={post.ogImage.url} />
              </Head>
              <PostHeader
                title={post.title}
                coverImage={post.coverImage}
                date={post.date}
                author={post.author}
              />
              <PostBody content={post.content} />
            </article>
            <SectionSeparator />
            {morePosts.length > 0 && <MoreStories posts={morePosts} />}
          </>
        )} */}
      </Container>
    </Layout>
  );
}

export async function getStaticProps({ params }) {
  const data = await getPostDataById(params.id);
  const content = await markdownToHtml(data?.content?.postContent || "");

  return {
    props: {
      post: {
        ...data?.content
      }
    }
  };
}

export async function getStaticPaths() {
  const allPosts = await getAllPostsWithId();

  return {
    paths: allPosts?.map(post => {
      return `/post/${post.content[0].id}`;
    }),
    fallback: true
  };
}
