import Container from "../components/container";
import MoreStories from "../components/more-stories";
import HeroPost from "../components/hero-post";
import Intro from "../components/intro";
import Layout from "../components/layout";
import { getAllPostsForHome } from "../lib/api";
import Head from "next/head";
import { CMS_NAME } from "../lib/constants";

export default function Index({ allArticles }) {
  const heroPost = allArticles[0];
  const morePosts = allArticles.slice(1);

  return (
    <>
      <Layout>
        <Head>
          <title>Next.js Blog Example with {CMS_NAME}</title>
        </Head>
        <nav className="main-navigation">
          <ul>
            <li>
              <a href="#home">Home</a>
            </li>
            <li>
              <a href="#posts">Posts</a>
            </li>
            <li>
              <a href="#contact">Contact</a>
            </li>
          </ul>
        </nav>
        <Container>
          {/* {morePosts.length > 0 && <MoreStories posts={morePosts} />} */}
          <div className="app">
            <Intro />
            {allArticles.map((blogPost, idx) => (
              <article key={idx} className="blogPost">
                {/* <HeroPost
                        title={post.title}
                        coverImage={post.coverImage}
                        date={post.createdAt}
                        author={post.author}
                        slug={post.slug}
                        excerpt={post.excerpt}
                      /> */}
                {blogPost.content.map(post => {
                  return (
                    <React.Fragment key={post.id}>
                      <div className="blogPost-header">
                        <h2 className="blogPost-title">
                          <a
                            href={`/post/${post.slug}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            {post.title}
                          </a>
                        </h2>
                        <div className="blogPost-meta">
                          <span>Posted on: {post.createdAt}</span> -{" "}
                          <span>Reading time: 4 minutes</span>
                        </div>
                      </div>
                      <div
                        className="blogPost-excerpt"
                        dangerouslySetInnerHTML={{ __html: post.excerpt }}
                      />
                      <div className="blogPost-table-content">
                        <h3>Table of Contents</h3>
                        <ul>
                          {post.tableOfContents &&
                            Object.entries(post.tableOfContents).map(item => {
                              return <li>{item[1]}</li>;
                            })}
                        </ul>
                      </div>
                      <div
                        className="blogPost-content"
                        dangerouslySetInnerHTML={{ __html: post.postContent }}
                      />
                      <footer className="blogPost-author">
                        Written by {post.author}
                      </footer>
                    </React.Fragment>
                  );
                })}
              </article>
            ))}
          </div>
        </Container>
      </Layout>
    </>
  );
}

export async function getStaticProps({ preview }) {
  const allArticles = (await getAllPostsForHome(preview)) || [];

  return {
    props: { allArticles }
  };
}
