import "isomorphic-unfetch";

const API_URL = "https://graphql.datocms.com";
const API_TOKEN = process.env.NEXT_EXAMPLE_CMS_DATOCMS_API_TOKEN;

// See: https://www.datocms.com/blog/offer-responsive-progressive-lqip-images-in-2020
const RESPONSIVE_IMAGE_FRAGMENT = `
  height
  sizes
  src
  srcSet
  width
  alt
  title
`;
const META_TAGS_FRAGMENT = `
  attributes
  content
  tag
`;

async function fetchAPI(query, { variables, preview } = {}) {
  const res = await fetch(API_URL + (preview ? "/preview" : ""), {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${API_TOKEN}`
    },
    body: JSON.stringify({
      query,
      variables
    })
  });

  const json = await res.json();
  if (json.errors) {
    console.error(json.errors);
    throw new Error("Failed to fetch API");
  }
  return json.data;
}

export async function getPostDataById(id) {
  const data = await fetchAPI(
    `
    query PostById($id: ItemId) {
      article(filter: {id: {eq: $id}}) {
        createdAt
        content {
          ... on PostRecord {
            id
          }
        }
      }
    }`,
    {
      variables: {
        id
      }
    }
  );
  return data?.post;
}

export async function getAllPostsWithSlug() {
  const data = fetchAPI(`
    {
      allArticles {
        content {
          slug
        }
      }
    }
  `);
  return data?.allPosts;
}

export async function getAllPostsForHome(preview) {
  const data = await fetchAPI(
    `
    {
      allArticles {
        content {
          ... on PostRecord {
            id
            title
            slug
            author
            postContent(markdown: true)
            excerpt
            tableOfContents
            createdAt
            coverImage {
              responsiveImage(imgixParams: { fit: crop, ar: "16:9", w: 750 }) {
                ${RESPONSIVE_IMAGE_FRAGMENT}
              }
            }
          }
        }
      }
    }
  `
  );

  return data?.allArticles;
}

