import Avatar from "../components/avatar";
import Date from "../components/date";
import CoverImage from "../components/cover-image";
import Link from "next/link";

export default function HeroPost({
  title,
  coverImage,
  date,
  content,
  excerpt,
  author,
  slug
}) {
  return (
    <section>
      <div className="mb-8 md:mb-16">
        {coverImage && (
          <CoverImage
            title={title}
            responsiveImage={coverImage.responsiveImage}
            slug={slug}
          />
        )}
      </div>
      <div className="mb-20 md:mb-28">
        <div className="blogPost-header">
          <h2 className="blogPost-title">
            <Link as={`/posts/${slug}`} href="/posts/[slug]">
              <a className="hover:underline">{title}</a>
            </Link>
          </h2>
          <div className="blogPost-meta">
            Posted on: <Date dateString={date} />
            <span>Reading time: 4 minutes</span>
          </div>
        </div>
        <div className="blogPost-excerpt">
          <p className="text-lg leading-relaxed mb-4">{excerpt}</p>
        </div>
        {/* <div className="blogPost-table-content">
          <h3>Table of Contents</h3>
          <ul>
            {post.tableOfContents &&
              Object.entries(post.tableOfContents).map(item => {
                return <li>{item[1]}</li>;
              })}
          </ul>
        </div> */}
        <div
          className="blogPost-content"
          dangerouslySetInnerHTML={{ __html: content }}
        />
        <footer className="blogPost-author">Written by {author}</footer>
      </div>
    </section>
  );
}
