import Container from "../components/container";
import MoreStories from "../components/more-stories";
import HeroPost from "../components/hero-post";
import Intro from "../components/intro";
import Layout from "../components/layout";
import { getAllPostsForHome } from "../lib/api";
import Head from "next/head";
import { CMS_NAME } from "../lib/constants";

export default function Index({ allArticles }) {
  const heroPost = allArticles[0];
  const morePosts = allArticles.slice(1);

  return (
    <>
      <Layout>
        <Head>
          <title>Next.js Blog Example with {CMS_NAME}</title>
        </Head>
        <nav className="main-navigation">
          <ul>
            <li>
              <a href="#home">Home</a>
            </li>
            <li>
              <a href="#posts">Posts</a>
            </li>
            <li>
              <a href="#contact">Contact</a>
            </li>
          </ul>
        </nav>
        <Container>
          <div className="app">
            <Intro />
            {allArticles.map((blogPost, idx) => (
              <article key={idx} className="blogPost">
                {blogPost.content.map(post => {
                  return (
                    <React.Fragment key={post.id}>
                      <HeroPost
                        title={post.title}
                        coverImage={post.coverImage}
                        date={post.createdAt}
                        author={post.author}
                        slug={post.slug}
                        content={post.postContent}
                        tableOfContents={post.tableOfContents}
                        excerpt={post.excerpt}
                        showContent={false}
                        id={blogPost.id}
                      />
                    </React.Fragment>
                  );
                })}
              </article>
            ))}
            {/* {morePosts.length > 0 && <MoreStories posts={morePosts} />} */}
          </div>
        </Container>
      </Layout>
    </>
  );
}

export async function getStaticProps({ preview }) {
  const allArticles = (await getAllPostsForHome(preview)) || [];

  return {
    props: { allArticles }
  };
}
